SELECT AVG(l_extendedprice) AS avg_price FROM lineitem l JOIN orders o ON l.l_orderkey = o.o_orderkey JOIN customer c ON o.o_custkey = c.c_custkey WHERE c.c_region = 'AFRICA';
SELECT COUNT(*) AS supplier_count FROM supplier s JOIN nation n ON s.s_nationkey = n.n_nationkey WHERE n.n_region = 'EUROPE';
