SELECT n.n_name, r.r_name FROM nation n, region r WHERE n.n_regionkey = r.r_regionkey AND r.r_name = 'EUROPE';
SELECT o.o_clerk, COUNT(*) FROM orders o WHERE o.o_orderstatus = 'O' GROUP BY o.o_clerk;
SELECT s.s_name, s.s_address FROM supplier s WHERE s.s_address LIKE '%Industrial%' AND s.s_nationkey = 12;
SELECT p.p_brand, p.p_container, AVG(p.p_retailprice) FROM part p GROUP BY p.p_brand, p.p_container;
SELECT c.c_mktsegment, SUM(c.c_acctbal) FROM customer c GROUP BY c.c_mktsegment;